/**
 * LeetCode剑指 Offer 66. 构建乘积数组
 * @param {number[]} a
 * @return {number[]}
 */

var constructArr = function(a) {
    // 判断数组中是否存在0
    if (a.indexOf(0) == -1) {
        var sum = product(a)
        a.forEach((item, index) => a[index] = sum / item) 
        return a
    } 
    let arr = [].concat(a).sort((b, c) => b - c)
    // console.log(arr, a)
    // 若数组中仅存在一个0
    let tempIndex = arr.indexOf(0)
    // console.log(tempIndex)
    if ((arr[0] == 0 && arr[1] != 0) || (arr[0] < 0 && arr[tempIndex + 1] != 0)) {
        arr = arr.splice(1, arr.length)
        // console.log(arr)
        var sum = product(arr)
        a.forEach((item, index) => item == 0 ? (a[index] = sum) : (a[index] = 0))
    }
    if ((arr[0] == 0 && arr[1] == 0) || (arr[0] < 0 && arr[tempIndex + 1] == 0)) {
        a.forEach((item, index) => a[index] = 0)
    }
    return a
}

function product (a) {
    return a.reduce(function (prev, cur) {
        return prev * cur
    }, 1)
}

var arr = [1, 2, 3, 4, 5]
console.log(constructArr(arr))

var arr = [1, 2, 0, 4, 5]
console.log(constructArr(arr))

var arr = [1, 2, 0, 4, 0]
console.log(constructArr(arr))

